mapper.select:
SELECT
-- 级联表的字段 ,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.sex_types as sexTypes ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM xiangmu a left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.xiangmu_name like CONCAT('%',#{params.xiangmuName},'%')
and a.leix_types = #{params.leixTypes}
and a.yonghu_id = #{params.yonghuId}
and a.jiaoshi_id = #{params.jiaoshiId}
and a.shifou_types = #{params.shifouTypes}
and a.xiangmu_content like CONCAT('%',#{params.xiangmuContent},'%')
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.sex_types = #{params.sexTypes}
and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
